import React, { useEffect, useState } from 'react';
import './App_comp.css';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

function BodyAdmin(props) {
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);

  // useEffect to fetch users list on initial render
  useEffect(() => {
    fetch(`http://${props.server_ip}:3001/users/admin`)
      .then(response => response.json())
      .then(data => {
        setUsers(data);
      })
      .catch(error => {
        console.error('Error fetching users:', error);
      });
  }, []);

  const handleUserSelect = (user) => {
    setSelectedUser(user);
    setIsDropdownOpen(false);
  };

  const toggleDropdown = () => {
    setIsDropdownOpen(prevState => !prevState);
  };

  return (
    <div>
      <h1>Users List</h1>
      <div className="dropdown-container">
        <input
          type="text"
          placeholder="Select a user"
          value={selectedUser ? selectedUser.username : ""}
          onClick={toggleDropdown}
          readOnly
        />
        {isDropdownOpen && (
          <ul className="dropdown-menu">
            {users.map(user => (
              <li key={user._id} onClick={() => handleUserSelect(user)}>
                {user.username}
              </li>
            ))}
          </ul>
        )}
      </div>

      {selectedUser && (
        <div>
          <h2>Edit User: {selectedUser.username}</h2>
          {/* Add form or components for editing user details */}
        </div>
      )}
    </div>
  );
}

export default BodyAdmin;
