import React, { useEffect, useState } from 'react';
import './App_comp.css';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

function BodyAdmin(props) {
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);

  // useEffect to fetch users list on initial render
  useEffect(() => {
    fetch(`http://${props.server_ip}:3001/users/admin`)
      .then(response => response.json())
      .then(data => {
        setUsers(data);
      })
      .catch(error => {
        console.error('Error fetching users:', error);
      });
  }, []);

  const handleUserSelect = user => {
    setSelectedUser(user);
    setIsDropdownOpen(false);
  };

  const toggleDropdown = () => {
    setIsDropdownOpen(prevState => !prevState);
  };

  return (
    <div>
      <div className={`dropdown${isDropdownOpen ? ' show' : ''}`}>
        <button
          className={`btn btn-secondary dropdown-toggle${isDropdownOpen ? ' show' : ''}`}
          type="button"
          id="userDropdown"
          onClick={toggleDropdown}
        >
          Select User
        </button>
        <div
          className={`dropdown-menu dropdown-menu-right${isDropdownOpen ? ' show' : ''}`}
          aria-labelledby="userDropdown"
        >
          {users.map(user => (
            <a
              key={user._id}
              className="dropdown-item"
              href="#"
              onClick={() => handleUserSelect(user)}
            >
              {user.username}
            </a>
          ))}
        </div>
      </div>

      {selectedUser && (
        <div>
          <h3>Selected User: {selectedUser.username}</h3>
          {/* Display user details or allow editing here */}
        </div>
      )}
    </div>
  );
}

export default BodyAdmin;
