import React, { useEffect, useState } from 'react';
import './App_comp.css';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

function BodyAdmin(props) {
  const [users, setUsers] = useState([]);

  // useEffect to fetch users list on initial render
  useEffect(() => {
    fetch(`http://${props.server_ip}:3001/users/admin`)
      .then(response => response.json())
      .then(data => {
        setUsers(data);
      })
      .catch(error => {
        console.error('Error fetching users:', error);
      });
  }, []);

  const handleTaskContentChange = (event, userId, taskId) => {
    const updatedUsers = users.map(user => {
      if (user._id === userId) {
        const updatedTasks = user.tasks.map(task => {
          if (task.id === taskId) {
            return { ...task, content: event.target.value };
          }
          return task;
        });
        return { ...user, tasks: updatedTasks };
      }
      return user;
    });
    setUsers(updatedUsers);
  };

  const handleTaskDateChange = (date, userId, taskId) => {
    const updatedUsers = users.map(user => {
      if (user._id === userId) {
        const updatedTasks = user.tasks.map(task => {
          if (task.id === taskId) {
            return { ...task, date };
          }
          return task;
        });
        return { ...user, tasks: updatedTasks };
      }
      return user;
    });
    setUsers(updatedUsers);
  };

  const handleAddTask = (userId) => {
    const updatedUsers = users.map(user => {
      if (user._id === userId) {
        const newTaskId = user.tasks.length + 1;
        const newTaskObj = {
          id: newTaskId,
          content: 'New Task',
          date: new Date(),
        };
        const updatedTasks = [...user.tasks, newTaskObj];
        return { ...user, tasks: updatedTasks };
      }
      return user;
    });
    setUsers(updatedUsers);
  };

  return (
    <div className="container">
      <h3>All Users</h3>
      <table className="table">
        <thead>
          <tr>
            <th>Username</th>
            <th>Tasks</th>
          </tr>
        </thead>
        <tbody>
          {users.map(user => (
            <tr key={user._id}>
              <td>{user.username}</td>
              <td>
                {user.tasks.map((task, index) => (
                  <div key={task.id} className="task-container">
                    <div className="task-field">
                      <label>Task:</label>
                      <input
                        type="text"
                        value={task.content}
                        onChange={event => handleTaskContentChange(event, user._id, task.id)}
                      />
                    </div>
                    <div className="task-field container d-flex">
                      <label>Date:</label>
                      <DatePicker
                        selected={new Date(task.date)}
                        onChange={date => handleTaskDateChange(date, user._id, task.id)}
                        className="datepicker"
                        dateFormat="dd/MM/yyyy"
                      />
                    </div>
                    {index === user.tasks.length - 1 && (
                      <button
                        className="btn btn-primary btn-sm"
                        onClick={() => handleAddTask(user._id)}
                      >
                        Add Task
                      </button>
                    )}
                  </div>
                ))}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default BodyAdmin;
