import React, { useEffect, useState } from 'react';
import './App_comp.css';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

function BodyAdmin(props) {
  const [users, setUsers] = useState([]);

  // useEffect to fetch users list on initial render
  useEffect(() => {
    fetch(`http://${props.server_ip}:3001/users/admin`)
      .then(response => response.json())
      .then(data => {
        setUsers(data);
      })
      .catch(error => {
        console.error('Error fetching users:', error);
      });
  }, []);

  return (
    <div className="container">
      <h3>All Users</h3>
      <table className="table">
        <thead>
          <tr>
            <th>Username</th>
            <th>Email</th>
            {/* Add more table headers for additional user data */}
          </tr>
        </thead>
        <tbody>
          {users.map(user => (
            <tr key={user._id}>
              <td>{user.username}</td>
              <td>{user.tasks}</td>
              {/* Add more table cells for additional user data */}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default BodyAdmin;
